print           LDR r0, tx_fifo         ; Load Address of TX_FIFO into register 0
                ADD r2, r2, string      ; Load r2 with Address of 1st letter of string
print_loop      ADD r3, r3, #1          ; Increment r2 and r3
                ADD r2, r2, #1      
                LDR r1, r2              ; Load Next Letter (Memory at r2) into r1
                CMP r1, #0              ; Check if null terminator
                BEQ print_end           ; if null halt
                STR r1, r0              ; Store r1 into TX_FIFO (UART_TX)                         
                B print_loop            ; Else loop to Line 3
print_end       HLT
tx_fifo         0xF0000000              ; TX_FIFO = 0xF0000000
string_t1       "Completed Test: "
string_t2       "Successfully"
